const toggler=document.querySelector(".header__burger-toggler"),menuMobile=document.querySelector(".nav-menu-mobile"),body=document.body;function handlerBurger(e){(!e.target.closest(".nav-menu-mobile")&&menuMobile.classList.contains("active")||e.target===toggler)&&(toggler.classList.toggle("active"),menuMobile.classList.toggle("active"))}function handlerResize(e){parseFloat(getComputedStyle(document.documentElement).getPropertyValue("320px"))<window.innerWidth&&(toggler.classList.remove("active"),menuMobile.classList.remove("active"))}body.addEventListener("click",handlerBurger),window.addEventListener("resize",handlerResize);